import React, { Component } from 'react'
import Header from './Header';
import Sidebar from './Sidebar';

class Bisection extends React.Component {
    constructor() {
        super()
        this.state = {
            result: null,
        }
        this.elements = {}
        this.cal = this.cal.bind(this);
    }

    render() {
        const { result } = this.state
        return (
            <div>
                <Header/>
                <Sidebar />
                <div className="content">
                    <h1>BISECTION</h1><br></br>
                        <h3>Equation :</h3>
                        <input type="text" defaultValue={ '2x+1' } ref={ (el) => this.elements.equation = el }></input><br></br>
                        <h3>X<sub>L</sub> :</h3>
                        <input type="text" defaultValue={ '2' } ref={ (el) => this.elements.xl = el }></input><br></br>
                        <h3>X<sub>R</sub> :</h3>
                        <input type="text" defaultValue={ '1' } ref={ (el) => this.elements.xr = el }></input><br></br><br></br>
                        <button type="button" onClick={ this.cal }>Submit</button><br></br><br></br><br></br><br></br>

                        <p>{ result }</p>

                        <center><table>
                            <tr>
                                <th>Iteration</th>
                                <th>X<sub>M</sub>(old)</th>
                                <th>X<sub>L</sub></th>
                                <th>X<sub>R</sub></th>
                                <th>F(X<sub>L</sub>)</th>
                                <th>F(X<sub>R</sub>)</th>
                                <th>X<sub>M</sub>(new)</th>
                                <th>F(X<sub>M</sub>)</th>
                                <th>Error</th>
                            </tr>
                            <tr>
                                <td>0</td>
                                <td>0</td>
                                <td>0</td>
                            </tr>
                            <tr>
                                <td>0</td>
                                <td>0</td>
                                <td>0</td>
                            </tr>
                        </table></center>


                </div>

            </div>

        )
    }

    cal() {
        const xl = parseInt(this.elements.xl.value, 10)
        const xr = parseInt(this.elements.xr.value, 10)
        const x_old = xr;
        const xm = 0;
        const n = 0;
        const check = parseFloat(0.000000);

        do{
            xm = this.findxm(xl,xr);
            check = Math.abs((xm-x_old)/xm).toFixed(8);
            console.log(check);
            n++;
            console.log(n);
            // Create an empty <tr> element and add it to the 1st position of the table:
            var row = table.insertRow(n);

            // Insert new cells (<td> elements) at the 1st and 2nd position of the "new" <tr> element:
            var cell1 = row.insertCell(0);
            var cell2 = row.insertCell(1);
            var cell3 = row.insertCell(2);
            var cell4 = row.insertCell(3);
            var cell5 = row.insertCell(4);

            // Add some text to the new cells:
            cell1.innerHTML = n;
            cell2.innerHTML = xl;
            cell3.innerHTML = xr;
            cell4.innerHTML = xm;
            cell5.innerHTML = check;
            if(parseFloat(xl)<parseFloat(xr)){
                if(funcal(xl)<funcal(xr)){
                        if(funcal(xm)>0){
                                xr = xm
                        }
                        else if(funcal(xm)<0){
                                xl = xm
                        }
                }
                else if(funcal(xl)>funcal(xr)){
                        if(funcal(xm)<0){
                                xr = xm
                        }
                        else if(funcal(xm)>0){
                                xl = xm
                        }
                }
            }
                x_old = xm
        }while(check>0.00001)        

        this.setState({
            result: result
        });
    }

    findxm(xl,xr) {
        return (parseFloat(xl)+parseFloat(xr))/2
    }

    funcal(X) {
        const equation = parseInt(this.elements.equation.value, 10)
        // expr = math.compile(expression);
        // let scope = {x:parseFloat(X)};
        // return expr.eval(scope);
    }

}

