import React, { Component } from 'react'
import Header from './Header';
import Sidebar from './Sidebar';

class Bisection extends React.Component {
    constructor() {
        super()
        this.state = {
            result: null,
        }
        this.elements = {}
        this.onPlus = this.onPlus.bind(this);
    }

    onPlus() {
        const firstValue = parseInt(this.elements.firstValue.value, 10)
        const secondValue = parseInt(this.elements.secondValue.value, 10)
        const resultPlus = this.onPlus(firstValue, secondValue)
        this.setState({
            result: resultPlus,
        });
    }

    plus(firstValue, secondValue) {
        return firstValue + secondValue
    }

    render() {
        const { result } = this.state
        return (
            <div>
            <Header/>
            <Sidebar />
            <div className="content">
                <h1>Bisection</h1>
                <p>Enter Input</p>
                <input type="number" defaultValue={ 0 } ref={ (el) => this.elements.firstValue = el } /><br></br><br></br>
                <input type="number" defaultValue={ 0 } ref={ (el) => this.elements.secondValue = el } /><br></br><br></br>
                <button type="button" className='plus' onClick={ this.onPlus }>Click</button>
                <p className='result'>{ result }</p>
            </div>
            </div>
        )
    }
}

export default Bisection;
