import React, { Component } from 'react'
import Header from './Header';
import Sidebar from './Sidebar';
import * as math from 'mathjs';
import Plot from 'react-plotly.js';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

var X,Y;
class Bisection extends React.Component {
    constructor(props) {
        super(props);
        X=[];
        Y=[];
        this.state={Arr:[],xl:"",xr:"",equation:"",submitted:true};
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.BisectionMethod=this.BisectionMethod.bind(this);
    }
    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
        this.setState({submitted:true});
        this.setState({Arr:[]});
    }
    handleSubmit(event) {
        let str=this.state.equation;
        let c=0;
        for(let i=0;i<str.length;i++){
          if(str[i]==="x"||str[i]==="X"){
            c++;
          }
        }
        if((str.length!==0)&&(c!==0)&&this.state.submitted&&this.state.xl!==""&&this.state.xr!==""){
          this.BisectionMethod();
          this.setState({submitted:false});
        }
        event.preventDefault();
      }
      BisectionMethod(event){
        X=[];
        Y=[];
          var func=(x)=>{
              let scope={x:x}
              let code=math.compile(this.state.equation);
              return code.eval(scope);
            };
          var err=(x0,x1)=>{return Math.abs((x1-x0)/x1)*100};
          var arr;
          var data={xl:0,xr:0,xm:0,err:"",fxl:0,fxr:0,fxm:0,count:1};
          var t=true;
          var xOld=0;
          data.xl=parseFloat(this.state.xl);
          data.xr=parseFloat(this.state.xr);
          while(t){
            data.xm=(data.xl+data.xr)/2;
            data.fxl=(func(data.xl)).toFixed(6);
            data.fxr=(func(data.xr)).toFixed(6);
            data.fxm=(func(data.xm)).toFixed(6);
            data.err=err(xOld,data.xm);
            arr=this.state.Arr;
            arr.push({xl:data.xl,xr:data.xr,xm:data.xm,err:data.err,fxl:data.fxl,fxr:data.fxr,fxm:data.fxm,count:data.count});
            if(func(data.xm)===0||data.count===15){
              t=false;
            }
            else if(func(data.xl)*func(data.xm)<0){
              data.xr=data.xm;
            }
            else if(func(data.xl)*func(data.xm)>0){
              data.xl= data.xm;
            }
            data.count++;
            xOld=data.xm;
          }
          for(let i=this.state.xl;i<=this.state.xr;i++){
            X.push(i);
            Y.push(func(i));
          }
      }

    render() {
        const DataRow=(props)=>{return (<tr><td>{props.data.count}</td>
                                            <td>{props.data.xl}</td>
                                            <td>{props.data.xr}</td>
                                            <td>{props.data.xm}</td>
                                            <td>{props.data.fxl}</td>
                                            <td>{props.data.fxr}</td>
                                            <td>{props.data.fxm}</td>
                                            <td>{props.data.err}</td></tr>);}
        let rows=this.state.Arr.map(x =>{return <DataRow key={x.count} data={x}/>});
        return (
            <div>
                <Header/>
                <Sidebar />
                <div className="content">
                <form id="F" onSubmit={this.handleSubmit}>
                    <h1>BISECTION</h1><br></br>
                        <h3>Equation :</h3>
                        <input name="equation" type="text" value={this.state.equation} onChange={this.handleChange} placeholder="Enter Equation"></input><br></br>
                        <h3>X<sub>L</sub> :</h3>
                        <input name="xl" type="text" step="any" value={this.state.xl} onChange={this.handleChange} placeholder="Enter Xl"></input><br></br>
                        <h3>X<sub>R</sub> :</h3>
                        <input name="xr" type="number" step="any" value={this.state.xr} onChange={this.handleChange} placeholder="Enter Xr"></input><br></br><br></br>
                        <button type="submit" value="Submit">Submit</button><br></br><br></br><br></br><br></br>
                </form>

                        {(this.state.submitted)
                        ? <div></div>
                        : <div><div id="T">
                            <center><table>
                            <thead>
                              <tr><th>Iteration</th>
                                  <th>X<sub>L</sub> </th>
                                  <th>X<sub>R</sub> </th>
                                  <th>X<sub>M</sub> </th>
                                  <th>F(X<sub>L</sub>)</th>
                                  <th>F(X<sub>R</sub>)</th>
                                  <th>F(X<sub>M</sub>)</th>
                                  <th>Error</th>
                              </tr>
                            </thead>
                            <tbody>
                              {rows}
                            </tbody>
                        </table></center>
                        </div>
                        </div>

