import React, { Component } from 'react'
import Component2 from './Component2'

class Content extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
           answer: 0,   // เก็บผลลัพธ์
           text1: 0,
           text2: 0,
        }
        this.changetext=this.changetext.bind(this)
     }

     changetext(text1,text2){
        this.setState({text1: text1})
        this.setState({text2: text2})
     }

     onAnswer(answer) {
        // รับค่าจาก Component 2 ผ่าน props _onAnswer
        // update ข้อมูล answer ใน state
        // เมื่อ react รู้ว่า state มีการเปลี่ยนแปลงมันจะทำการ render ให้ใหม่
        this.setState({answer: answer})
     }

    render() {
        const { answer, text1, text2 } = this.state
        return (
            <div className="content">
                <p>Enter Input</p>
                <input type="text" text1={text1} />
                <input type="text" text2={text2} />
                <button>Click</button>
            </div>
        )
    }
}

export default Content;
