import React, { Component } from 'react'
import Header from './Header';
import Sidebar from './Sidebar';
import * as math from 'mathjs';
import Plot from 'react-plotly.js';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

var X,Y;
class Cramer extends React.Component {
    constructor(props) {
        super(props);
        X=[];
        Y=[];
        this.state={Arr:[],variable:"",submitted:true}; //ประกาศตัวแปร xl xr และ equation
        this.handleSubmit = this.handleSubmit.bind(this); //ประกาศฟังก์ชัน handleSubmit
        this.handleChange = this.handleChange.bind(this); //ประกาศฟังก์ชัน handleChange
        this.CramerMethod=this.CramerMethod.bind(this); //ประกาศฟังก์ชัน CramerMethod
        this.variable=this.variable.bind(this);
    }
    handleChange(event) {
        this.setState({[event.target.name]: event.target.value}); //ทำการเซตค่า xl xr equation ใหม่ให้เป็นไปตามที่ผู้ใช้กรอก
        this.setState({submitted:true});
        this.setState({Arr:[]});
    }
    handleSubmit(event) {
        if((this.state.variable!==0)&&this.state.submitted=="true"){ //เช็คความยาวสมการต้องไม่เท่ากับ 0 และ c ที่เช็ค x ต้องไม่เท่ากับ 0 และ xl xr ต้องไม่ใช่ค่าว่าง
            this.variable();
            this.setState({submitted:false});
        }
        event.preventDefault();
    }
    variable(event){
        let variable=this.state.variable;
        var a=[],ans=[]
        for( var i=0; i<variable; i++ ) {
            a.push( [] );
        }
        for(var i=0;i<variable;i++){
            for(var j=0;j<variable;j++){
                a[i].push(document.createElement("INPUT"));
                a[i][j].setAttribute("type", "text");
                a[i][j].setAttribute("id", "in"+i+""+j);
            }
        }
        for(var i=0;i<variable;i++){
            ans.push(document.createElement("INPUT"));
            ans[i].setAttribute("type", "text");
            ans[i].setAttribute("id", "ans"+i);
        }
        for(var i=0;i<variable;i++){
            var para = document.createElement("div");
            para.setAttribute("class", "row");

            for(var j=0;j<variable;j++){
                var col= document.createElement("div");
                col.appendChild(a[i][j]);
                a[i][j].setAttribute("placeholder", "X"+(j+1));
                para.appendChild(col)
            }
            para.insertAdjacentHTML( 'beforeend'," = ");
            var col= document.createElement("div");
            col.appendChild(ans[i]);
            ans[i].setAttribute("placeholder", "B"+(i+1));
            para.appendChild(col)

            document.getElementById("myDiv").appendChild(para);
          }

        var  sum= document.createElement("INPUT");
        sum.setAttribute("type","submit")
        sum.setAttribute("value","submit")
        sum.setAttribute("id","sum")

        document.getElementById("myDiv").appendChild(sum);
    }
    CramerMethod(event){

    }

    render() {
        const DataRow=(props)=>{return (<tr><td>{props.data.count}</td> 
                                            <td>{props.data.x0}</td>
                                            <td>{props.data.err}</td></tr>);}
                                            //ประกาศให้ DataRow เก็บค่าที่จะแสดงในตารางเอาไว้ แล้วส่งไปเมื่อมีคนเรียกใช้
        let rows=this.state.Arr.map(x =>{return <DataRow key={x.count} data={x}/>}); 
        return (
            <div>
                <Header/>
                <Sidebar />
                <div className="content">
                <form id="F" onSubmit={this.handleSubmit}>
                    <h1>CRAMER'S RULE</h1><br></br>
                        <h3>Enter number of variable:</h3>
                        <input name="variable" type="number" value={this.state.variable} onChange={this.handleChange} placeholder="Enter number of variable"></input><br></br>
                        <button type="submit" value="Submit">Submit</button><br></br><br></br><br></br><br></br>
                </form>

                <div>
                    <form>
                        <div id="myDiv"></div>
                    </form>
                </div>

                </div>

            </div>
        )
    }
}
export default Cramer;
