import React, { Component } from 'react'
import Header from './Header';
import Sidebar from './Sidebar';
import {
  Button,
  Table
} from 'antd';
import * as math from 'mathjs';
import Plot from 'react-plotly.js';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';
import axios from 'axios';

const header = [{
  title: 'Iteration',
  dataIndex: 'iteration',
}, {
  title: 'Xl',
  dataIndex: 'xl',
}, {
  title: 'Xr',
  dataIndex: 'xr',
}, {
  title: 'Xm',
  dataIndex: 'xm',
}, {
  title: 'fxl',
  dataIndex: 'fxl',
}, {
  title: 'fxr',
  dataIndex: 'fxr',
}, {
  title: 'fxm',
  dataIndex: 'fxm',
}, {
  title: 'Error',
  dataIndex: 'Error',
}];;

var X,Y;
class Bisection extends React.Component {
  state = {
    result: [],
    equation: "",
    Xr: 0,
    Xl: 0,
    output: 0,
    Xm: 0,
    condition: ""
  }
  input = (x) => {
    this.setState({
      [x.target.name]: x.target.value
    })
  }
  cal = () => {
    axios
      .post("http://localhost:8000/bisec/bisec", {
        xl: parseFloat(this.state.Xl),
        xr: parseFloat(this.state.Xr),
        equation: this.state.equation
      })
      .then(res => {
        this.setState({result:res.data.result})
        console.log(this.state.result)
      })
      .catch(err => {
        console.log(err);
      });

  }

    render() {
        const DataRow=(props)=>{return (<tr><td>{props.data.count}</td> 
                                            <td>{props.data.xl}</td>
                                            <td>{props.data.xr}</td>
                                            <td>{props.data.xm}</td>
                                            <td>{props.data.fxl}</td>
                                            <td>{props.data.fxr}</td>
                                            <td>{props.data.fxm}</td>
                                            <td>{props.data.err}</td></tr>);}
                                            //ประกาศให้ DataRow เก็บค่าที่จะแสดงในตารางเอาไว้ แล้วส่งไปเมื่อมีคนเรียกใช้
        let rows=this.state.Arr.map(x =>{return <DataRow key={x.count} data={x}/>}); 
        return (
            <div>
                <Header/>
                <Sidebar />
                <div className="content">
                <form id="F">
                    <h1>BISECTION</h1><br></br>
                        <h3>Equation :</h3>
                        <input name="equation" type="text" onChange={this.input.bind(this)} placeholder="Enter Equation"></input><br></br>
                        <h3>X<sub>L</sub> :</h3>
                        <input name="Xl" type="text" step="any" onChange={this.input.bind(this)} placeholder="Enter Xl"></input><br></br>
                        <h3>X<sub>R</sub> :</h3>
                        <input name="Xr" type="text" step="any" onChange={this.input.bind(this)} placeholder="Enter Xr"></input><br></br><br></br>
                        <button type="submit" value="Submit" onClick={this.cal.bind(this)}>Submit</button><br></br><br></br><br></br><br></br>
                </form>

                        {(this.state.submitted)
                        ? <div></div>
                        : <div><div id="T">
                            <center><table>
                            <thead>
                              <tr><th>Iteration</th>
                                  <th>X<sub>L</sub> </th>
                                  <th>X<sub>R</sub> </th>
                                  <th>X<sub>M</sub> </th>
                                  <th>F(X<sub>L</sub>)</th>
                                  <th>F(X<sub>R</sub>)</th>
                                  <th>F(X<sub>M</sub>)</th>
                                  <th>Error</th>
                              </tr>
                            </thead>
                            <tbody>
                              {<Table style={{width:"800px",margin:"auto"}} dataSource={this.state.Arr} columns={header} pagination={false} />}
                            </tbody>
                        </table></center>
                        </div>
                        </div>

                        }

                </div>

            </div>
        )
    }
}
export default Bisection;
