import React, { Component } from 'react'
import Header from './Header';
import Sidebar from './Sidebar';
import {Input} from 'antd';
import * as math from 'mathjs';

var MatrixA=[],MatrixB=[],Output=[],A=[],B=[],C=0;
class Cramer extends React.Component {
    constructor(props) {
        super(props);
        this.state={showMatrix:false,showOutput:false,N:""};
        this.handleChange=this.handleChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
        this.CramerMethod=this.CramerMethod.bind(this);
    }
    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
        this.setState({showMatrix:false,showOutput:false});
        MatrixA=[];
        MatrixB=[];
    }
    handleSubmit(event) {
        let va=parseInt(this.state.variable);
        if(N!==0||N!==""){
            this.createMatrix(N);
            this.setState({showMatrix:true});
        }
        event.preventDefault();is.setState({submitted:true});
    }
    createMatrix(n) {
        MatrixA=[];
        MatrixB=[];
            for (var i=1 ; i<=n ; i++) {
                for (var j=1 ; j<=n ; j++) {
                    MatrixA.push(<Input style={{
                        width: "10%",
                        height: "40%",
                        fontSize: "18px",
                        marginInlineEnd: "5%",
                        marginBlockEnd: "5%",
                    }}
                    className="input is-primary"
                    id={"a"+i+""+j} key={"a"+i+""+j}
                    placeholder={"a"+i+""+j} />)
                }
                MatrixA.push(<br key={"br"+i}/>)
                MatrixB.push(<Input style={{
                    width: "10%",
                    height: "40%",
                    fontSize: "18px",
                    marginInlineEnd: "5%",
                    marginBlockEnd: "5%",
                }}
                className="input is-danger"
                id={"b"+i} key={"b"+i}
                placeholder={"b"+i} />)
            }
    }
    initMatrix() {
        A=[];
        B=[];
        C=0;
        Output=[];
          for(var i=0 ; i<this.state.N ; i++) {
              A[i] = []
              for(var j=0 ; j<this.state.N ; j++) {
                  A[i][j] = (parseFloat(document.getElementById("a"+(i+1)+""+(j+1)).value));
              }
              B.push(parseFloat(document.getElementById("b"+(i+1)).value));
          }
    }
    CramerMethod(event){
        this.initMatrix();
        var count=0;
        while (count < this.state.N) {
            var transformMatrix = JSON.parse(JSON.stringify(A));
            for (var i=0 ; i<this.state.N ; i++) {
                for (var j=0 ; j<this.state.N ; j++) {
                    if (j === count) {
                        transformMatrix[i][j] = B[i]
                        break;
                    }
                }
            }
            count++;
            Output.push((math.det(transformMatrix)/math.det(A)).toFixed(1));
        }
        for(let k=0;k<this.state.N;k++){
        console.log(Output[k]);
        }
        this.setState({showOutput: true});
    }

    render() {
        const DataRow=(props)=>{return (<tr><td>{C+1}</td>
                                        <td>{Output[C++]}</td>
                                        {/* <td><progress class="progress is-danger" value={props.data.err} max="100">{props.data.err}%</progress></td> */}
                                        </tr>);
                                        }
        let rows=Output.map(x =>{return <DataRow key={C+1}/>}); 
        return (
            <div>
                <Header/>
                <Sidebar />
                <div className="content">
                <form id="F" onSubmit={this.handleSubmit}>
                    <h1>CRAMER'S RULE</h1><br></br>
                        <h3>Enter number of variable:</h3>
                        <input name="variable" type="text" value={this.state.variable} onChange={this.handleChange} placeholder="Enter number of variable"></input><br></br><br></br>
                        <button type="submit" value="Submit">Submit</button><br></br><br></br><br></br><br></br>
                </form>

                <form id="form2">
                     <div id="myDiv"></div>
                </form>

                </div>

            </div>
        )
    }
}
export default Cramer;
