        return (
            <div className="content">
                <h1>WELCOME</h1>
                <img src ="img/logonu" alt="logo" width="500" height="333"/>
                <img src="/img/logoooo.jpg" alt="logo" width="500" height="333"/>
                <img src="./logoooo.jpg" alt="logo" width="500" height="333"/>
                <img src="./img/logoooo.jpg" alt="logo" width="500" height="333"/>
                <img src="/img/logoooo" alt="logo" width="500" height="333"/>
            </div>
        )
    }
}
export default Content;























