        for(let i=0;i<n-1;i++){
            for(let j=i+1;j<n;j++){
                var ratio=A[j][i]/A[i][i];
                for(let k=i+1;k<n;k++){
                    A[j][k]=A[j][k]-ratio*A[i][k];
                }
                B[j]=B[j]-ratio*B[i];
                for(let j=i+1;j<n;j++){
                    A[j][i]=0;
                }
            }
        }

    //Backward
        X = new Array(n);
        X[n-1] = B[n-1] / A[n-1][n-1];
        for(i=n-2 ; i>=0 ; i--) {
