import React, { Component } from 'react'
import Header from './Header';
import Sidebar from './Sidebar';
import {Input} from 'antd';
import * as math from 'mathjs';

var MatrixA=[],MatrixB=[],Output=[],A=[],B=[],C=0;
class Cramer extends React.Component {
    constructor(props) {
        super(props);
        this.state={showMatrix:false,showOutput:false,N:""};
        this.handleChange=this.handleChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
        this.CramerMethod=this.CramerMethod.bind(this);
    }
    handleChange(event) {
        this.setState({[event.target.name]: event.target.value}); //ทำการเซตค่า xl xr equation ใหม่ให้เป็นไปตามที่ผู้ใช้กรอก
        this.setState({submitted:true});
        this.setState({Arr:[]});
    }
    handleSubmit(event) {
        let str=this.state.variable;
        let c=0;
        if((str.length!==0)&&(c==0)&&this.state.submitted==true){ //เช็คความยาวสมการต้องไม่เท่ากับ 0 และ c ที่เช็ค x ต้องไม่เท่ากับ 0 และ xl xr ต้องไม่ใช่ค่าว่าง
            this.variable();
            this.setState({submitted:false});
            event.preventDefault();
            c++;
        }else{
            c=0;
            this.setState({submitted:true});
        }
    }
    variable(event){
        let variable=this.state.variable;
        var a=[],ans=[]
        for( var i=0; i<variable; i++ ) {
            a.push( [] );
        }
        for(var i=0;i<variable;i++){
            for(var j=0;j<variable;j++){
                a[i].push(document.createElement("INPUT"));
                a[i][j].setAttribute("type", "text");
                a[i][j].setAttribute("id", "in"+i+""+j);
            }
        }
        for(var i=0;i<variable;i++){
            ans.push(document.createElement("INPUT"));
            ans[i].setAttribute("type", "text");
            ans[i].setAttribute("id", "ans"+i);
        }
        for(var i=0;i<variable;i++){
            var para = document.createElement("div");
            para.setAttribute("class", "row");

            for(var j=0;j<variable;j++){
                var col= document.createElement("div");
                col.appendChild(a[i][j]);
                a[i][j].setAttribute("placeholder", "X"+(j+1));
                para.appendChild(col)
            }
            para.insertAdjacentHTML( 'beforeend'," = ");
            var col= document.createElement("div");
            col.appendChild(ans[i]);
            ans[i].setAttribute("placeholder", "B"+(i+1));
            para.appendChild(col)

            document.getElementById("myDiv").appendChild(para);
          }

        var  sum= document.createElement("INPUT");
        sum.setAttribute("type","submit")
        sum.setAttribute("value","submit")
        sum.setAttribute("id","sum")

        document.getElementById("myDiv").appendChild(sum);
        document.getElementById('form2').onsubmit = function (event) {
            event.preventDefault()
            this.CramerMethod();
        }
    }
    CramerMethod(event){
        var cramersRule=(matrix,freeTerms)=>{ //ฟังก์ชันย่อยรับค่ามาแล้วส่งไปแทนในสมการ
            let det = detr(matrix),
            returnArray = [],
            i,
            tmpMatrix;

            for(i=0; i < matrix[0].length; i++) {
                let tmpMatrix = insertInTerms(matrix, freeTerms,i)
                returnArray.push(detr(tmpMatrix)/det)
            }
            return returnArray;
        };

        var insertInTerms=(matrix, ins, at)=>{
            let tmpMatrix = clone(matrix),
            i;
            for(i=0; i < matrix.length; i++) {
              tmpMatrix[i][at] = ins[i];
            }
            return tmpMatrix;
        };

        var detr=(m)=>{
            let temp = 0;
            let ret = 1,
              k,
              A = clone(m),
              n = m[0].length,
              alpha;

            for(var j =0; j < n-1; j++) {
              k=j;
              for(i=j+1;i<n;i++) { if(Math.abs(A[i][j]) > Math.abs(A[k][j])) { k = i; } }
              if(k !== j) {
                  temp = A[k]; A[k] = A[j]; A[j] = temp;
                  ret *= -1;
              }
              var Aj = A[j];
              for(i=j+1;i<n;i++) {
                var Ai = A[i];
                       alpha = Ai[j]/Aj[j];
                       for(k=j+1;k<n-1;k+=2) {
                           var k1 = k+1;
                           Ai[k] -= Aj[k]*alpha;
                           Ai[k1] -= Aj[k1]*alpha;
                       }
                       if(k!==n) { Ai[k] -= Aj[k]*alpha; }
                   }
                   if(Aj[j] === 0) { return 0; }
                   ret *= Aj[j];
                }
               return Math.round(ret*A[j][j]*100)/100;
        };

        var clone=(m)=>{
            return m.map(function(a){return a.slice();});
        };

        var variable=this.state.variable;
        var matrix=[],answer=[]
        for( var i=0; i<variable; i++ ) {
            matrix.push( [] );
        }

        for(var i=0;i<variable;i++){
            for(var j=0;j<variable;j++){
                matrix[i].push(parseFloat(document.getElementById("in"+i+j).value))
            }
        }
        console.log(matrix)
        for(var i=0;i<variable;i++){
            answer.push(parseFloat(document.getElementById("ans"+i).value))
        }
        console.log(answer)
        var result = cramersRule(matrix,answer);
        console.log(result);
        var html =  "<thead><tr>"
        for(var i=0; i<result .length; i++){
            html += "<th>X<sub>" + (i+1) + "</sub></th>";
        }

        html+="</tr></thead><tr>"

        for (var i=0; i<result .length; i++) {
            html += "<td>" + result[i] + "</td>";
        }

        // ATTACH HTML TO CONTAINER
        document.getElementsByName("show")[0].innerHTML = html;
    }

    render() {
        const DataRow=(props)=>{return (<tr><td>{props.data.count}</td> 
                                            <td>{props.data.x0}</td>
                                            <td>{props.data.err}</td></tr>);}
                                            //ประกาศให้ DataRow เก็บค่าที่จะแสดงในตารางเอาไว้ แล้วส่งไปเมื่อมีคนเรียกใช้
        let rows=this.state.Arr.map(x =>{return <DataRow key={x.count} data={x}/>}); 
        return (
            <div>
                <Header/>
                <Sidebar />
                <div className="content">
                <form id="F" onSubmit={this.handleSubmit}>
                    <h1>CRAMER'S RULE</h1><br></br>
                        <h3>Enter number of variable:</h3>
                        <input name="variable" type="text" value={this.state.variable} onChange={this.handleChange} placeholder="Enter number of variable"></input><br></br><br></br>
                        <button type="submit" value="Submit">Submit</button><br></br><br></br><br></br><br></br>
                </form>

                <form id="form2">
                     <div id="myDiv"></div>
                </form>

                </div>

            </div>
        )
    }
}
export default Cramer;
